/**
 * This script removes all .info.mdx files that are generated by the OpenAPI plugin
 * Run it after the build with: 'npm run postbuild' or 'node cleanup-info-mdx.js'.
 */

const fs = require('fs');
const path = require('path');
const glob = require('glob');

// Find all .info.mdx files in the SDK-OpenAPI directory
const infoFiles = glob.sync('docs/SDK-OpenAPI/**/*.info.mdx');

console.log(`Found ${infoFiles.length} .info.mdx files to clean up:`);

// Remove each file
infoFiles.forEach(file => {
  console.log(`Removing ${file}`);
  fs.unlinkSync(file);
});

console.log('Cleanup complete!');
